const AmountOfCardsbyLvl=[6,12,20],grid=document.querySelector(".grid"),currentLvl=1;function setBoard(){let d=[];for(let e=0;e<AmountOfCardsbyLvl[currentLvl];e++){let c="https://api.adorable.io/avatars/"+e;d.push({id:e,src:c})}(d=d.concat(d).sort(function(){return.5-Math.random()})).forEach(function(d){const e=document.createElement("div");e.classList.add("card"),e.setAttribute("data-card",d.id);const c=document.createElement("img");c.src="./app/img/question-mark.png",c.classList.add("card__side"),c.classList.add("card__side--back");const t=document.createElement("img");t.src=d.src,t.classList.add("card__side"),t.classList.add("card__side--front"),e.appendChild(c),e.appendChild(t),grid.appendChild(e)})}setBoard();let cards=document.querySelectorAll(".card"),cardsFlipped=document.querySelectorAll(".card--flipped");cards.forEach(function(d){d.addEventListener("click",function(){!cardsFlipped||cardsFlipped.length<2?(d.classList.toggle("card--flipped"),cardsFlipped=document.querySelectorAll(".card--flipped")):(cardsFlipped.forEach(function(d){d.classList.remove("card--flipped")}),cardsFlipped=void 0)})});